package org.servicebroker.sourcecontrol.config;

import org.openpaas.servicebroker.model.Catalog;
import org.openpaas.servicebroker.model.Plan;
import org.openpaas.servicebroker.model.ServiceDefinition;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;

import java.util.Arrays;
import java.util.HashMap;
import java.util.Map;

/**
 * Catalog API 에서 사용하는 Catalog Bean 생성
 *
 * Created by lena on 2017-05-16.
 */
@Configuration
public class CatalogConfig {
	
	@Bean
	public Catalog catalog() {

		/**
		 * (*)id : 식별자 (GUID 사용 권장)
		 * (*)name : 카탈로그 표시되는 service 이름 (소문자, 공백 없음)
		 * (*)description : 서비스 설명
		 * (*)bindable : Application 에 서비스를 바인드 할 수 있는지 여부
		 * (*)plans : Plans 스키마 정의
		 * tags
		 * metadata : 서비스 제공을 위한 Service Metadata 목록
		 * requires : 서비스 제공하는 권한 목록 (현재 syslog_drain권한만 지원)
		 * dashboard_client : 대시보드 SSO 기능을 활성화 하는데 필요한 데이터
		 * */
		return new Catalog( Arrays.asList(
				new ServiceDefinition(
					"ap-sourcecontrol-service-id",
					"p-ap-sourcecontrol",
					"A Application Platform source control service for application development.provision parameters : parameters {owner : {owner}, org_name : {org_name}}",
					false,
					false,
					Arrays.asList(
							new Plan("default-plan-id",
									"Default",
									"This is a default service plan. All services are created equally.",
									null)),
					Arrays.asList("ap-sourcecontrol"),
					getServiceDefinitionMetadata(),
					null,
					null)));
	}
	
	/* Used by Pivotal CF console */
	private Map<String,Object> getServiceDefinitionMetadata() {
		// Service Metadata
		Map<String,Object> sdMetadata = new HashMap<String,Object>();
		sdMetadata.put("displayName", "p-ap-sourcecontrol");
		sdMetadata.put("imageUrl","data:image/jpeg;base64,");
		sdMetadata.put("longDescription","Application Platform Source Control Service");
		sdMetadata.put("providerDisplayName","K-PaaS");
		sdMetadata.put("documentationUrl","https://k-paas.or.kr");
		sdMetadata.put("supportUrl","https://k-paas.or.kr");
		return sdMetadata;
	}
}
